export default {
  name: 'apache_echartsa',
  data() {
    return {};
  },
  mounted() {
    this.barEcharts();
  },
  methods: {
    barEcharts() {
      // 基于准备好的dom，初始化echarts实例
      var myChart = echarts.init(document.getElementById('mai'));
      // 指定图表的配置项和数据
      var option = {
        backgroundColor: '#000',
        globe: {
          baseTexture: ROOT_PATH + '/data-gl/asset/earth.jpg',
          heightTexture: ROOT_PATH + '/data-gl/asset/bathymetry_bw_composite_4k.jpg',
          displacementScale: 0.1,
          shading: 'lambert',
          environment: ROOT_PATH + '/data-gl/asset/starfield.jpg',
          light: {
            ambient: {
              intensity: 0.1
            },
            main: {
              intensity: 1.5
            }
          },
          layers: [{
            type: 'blend',
            blendTo: 'emission',
            texture: ROOT_PATH + '/data-gl/asset/night.jpg'
          }, {
            type: 'overlay',
            texture: ROOT_PATH + '/data-gl/asset/clouds.png',
            shading: 'lambert',
            distance: 5
          }]
        },
        series: []
      };
      // 使用刚指定的配置项和数据显示图表。
      myChart.setOption(option);
    }
  }
